import React, { Component } from 'react';
import { useHistory } from "react-router-dom";
import InputWithLabel from '../../componentes/InputWithLabel/InputWithLabel';
import classes from './inicio.css';

import { ReactComponent as House } from './login.svg';

import {
    Link
} from "react-router-dom";

class Inicio extends Component {
    
    state = {
        email: "",
        password: "",

        rut:"",
    }

    verify() {
        if (this.state.email === "admin" && this.state.password === "admin") {
            console.log("in");
            this.setState({rut:'admin'});
        }
        else if (this.state.email === "resd" && this.state.password === "resd") {
            console.log("in");
        }
    }
    render() {
        return (
            <div className={classes.inicioContainer}>
                <div>
                    <InputWithLabel label="Correo" onChange={(newValue) => this.setState({ email: newValue })}></InputWithLabel>
                    <InputWithLabel label="ContraseÃ±a" onChange={(newValue) => this.setState({ password: newValue })}></InputWithLabel>

                </div>
                <div>
                    <Link to={`/${this.state.rut}`}>
                    <button className={classes.inputButton} onClick={this.verify()} >Iniciar</button>
                    </Link>
                </div>
                <House className={classes.bg} />

            </div>

        )
    }
}


export default Inicio;
