import React, { Component } from 'react';
import Button from "@material-ui/core/Button";
import { Link, withRouter } from "react-router-dom"
import classes from "../Foro/PostComplete.css"

class PostComplete extends Component {

    state = {
        comments: [],
        comment:'',
    }

    handleAddcomment = (comment) => {
        this.setState({
            comments: [...this.state.comments, comment]
        })
        console.log(this.state)
    }
    handleInput = (e) =>{
        const {value,name }=e.target;
         this.setState({
           [name]:value
        })
     };
     handleSubmit=(e)=>{
        e.preventDefault();
        this.handleAddcomment(this.state)
        console.log(this.state)
        this.setState({
            comment: '',
          });
    }

    render() {

        var post = this.props.post
        const comts = this.state.comments.map((comment, i) => {

            return (
              <div>
                  <p>{comment}</p>
              </div>
            )
        })

        return (
            <div className={classes['body']}>
                <h2 className={classes['title']}>{post.title}</h2>
                <p>{post.Topic}</p>
                <input
                    className={classes.inputs}
                    type="text"
                    name="title"
                    onChange={this.handleInput}
                    placeholder="Comentario"></input>

                    {comts}


                <Link to='/Foro'>
                    <Button m={2}
                        size="large"
                        color="primary"
                        variant="contained"
                        onClick={this.handleSubmit}
                    >Agregar Comentario</Button>
                </Link>

                <Link to='/Foro'>
                    <Button m={2}
                        size="large"
                        color="primary"
                        variant="contained"
                    >Regresar</Button>
                </Link>
            </div>
        )
    }
}

export default withRouter(PostComplete);
