import React, { Component } from 'react';
import ForoPost from '../../componentes/ForoPost/ForoPost.js'
import classes from '../Foro/Foro.css'
import axios from 'axios'

class Foro extends Component {

    state = {
        posts: []
    }
    componentDidMount() {
        axios.get('./Foro.json')
            .then(res => {
                console.log(res)

                this.setState({
                    posts: res.data.slice(0, 6)
                })
            })
    }


    render() {

        const { posts } = this.state;
        const postList = posts.length ? (

            posts.map(post => {
                return (

                    <tr className={classes['body']}>
                        <td>
                            <h4>{post.title}</h4>
                        </td>
                        <td>
                            <p>{post.date}</p>
                        </td>
                        <td>
                            <p>{post.hour}</p>
                        </td>
                        <td>
                            <p>{post.author}</p>
                        </td>
                    </tr>
                )
            })
        ) : (
                <div>No hay registros de pago</div>
            )
        return (
            <div className={classes['body']}>
                <h2 className={classes['title']}>Foro Residencial</h2>
                <table className={classes['table']} >
                    <tr className={classes['headerT']}>

                        <td><th>Tema</th></td>
                        <td><th>Fecha</th></td>
                        <td><th>Hora</th></td>
                        <td><th>Autor</th></td>
                    </tr>
                    {postList}
                </table>

            </div>
        )
    }
}
export default Foro
