import React from 'react';
import './App.css';
import ListItems from './ListItems';
import { library } from '@fortawesome/fontawesome-svg-core';
import {faTrash} from '@fortawesome/free-solid-svg-icons';

library.add(faTrash);

class App extends React.Component{
  constructor(props){
    super(props); // Se llama a la clase base
    this.state = { // Se define el estado. El segundo es el objeto que almacena 
      items:[],
      currentItem:{
        text:'',
        key:''
      }
    } // **** Se soluciona uniendo handleInput al constructor
    this.addItem = this.addItem.bind(this);
    this.handleInput = this.handleInput.bind(this);
    this.deleteItem = this.deleteItem.bind(this);
    this.setUpdate = this.setUpdate.bind(this);
  }
  handleInput(e){
    this.setState({  // ****handleInput pierde su contexto porque la keyword no apunta a la la clase automáticamente ****
      currentItem:{
        text: e.target.value,
        key:Date.now()
      }
    })   // Para cambiar el estado de la variable
         // No se puede actualizar un estado directamente. Se debe usar
         // this.setState()
  }
  addItem(e){
    e.preventDefault();
    const newItem = this.state.currentItem;
    console.log(newItem);
    if(newItem){
      const newItems = [...this.state.items, newItem]; //Destructurim Assignment4
      this.setState({
        items:newItems,
        currentItem:{
          text:'',
          key:''
        }
      })
    }
  }
  deleteItem(key){
    const filteredItems= this.state.items.filter(item =>
      item.key!==key);
    this.setState({
      items: filteredItems
    })
  }
  setUpdate(text, key){
    const items = this.state.items;
    items.map(item => {
      if(item.key===key){
        item.text = text;
      }
    })
    this.setState({
      items: items
    })
  }
  render(){
    return(
        <div className = "App">
          <header>
            <form id = "to-do-form" onSubmit = {this.addItem}>
              <input type = "text" placeholder = "Enter text"
              value = {this.state.currentItem.text}
              onChange = {this.handleInput}/>
              <button type = "submit">Añadir</button>
            </form>
          </header>
          <ListItems items = {this.state.items}
            deleteItem = {this.deleteItem}
            setUpdate = {this.setUpdate}/>
        </div>
      );
    }
  }

export default App;
